SELECT -- COUNT(ss.idsolicitacaoservico) AS total_tickets_portifolio ss.idsolicitacaoservico AS ticket, ans.titulosla AS ans_firmado, p.descportfolioservico as portifolio  FROM itsm.solicitacaoservico ss  INNER JOIN itsm.servicocontrato sc ON sc.idservicocontrato = ss.idservicocontrato  INNER JOIN itsm.servico s ON s.idservico = sc.idservico  INNER JOIN itsm.servicoautorelacionamento sar ON sar.idservico = sc.idservico  INNER JOIN itsm.tempoatendservicocontrato tsc ON tsc.idservicocontrato = sc.idservicocontrato INNER JOIN itsm.acordonivelservico ans ON ans.idacordonivelservico = tsc.idacordonivelservico INNER join itsm.portfolioservico p on p.idportfolioservico = s.idportfolioservico AND p.idportfolioservico = 4 WHERE 1=1 AND ans.idacordonivelservico = 4 --Ans a combinar